import { useRef } from 'react';
import { useState } from 'react';
import appStyles from './App.module.css';
import useBookSearch from './useBookSearch';

function App() {
  const [query, setQuery] = useState("");
  const [pageNumber, setPageNumber] = useState(1);

  const { loading, books, hasMore, error } = useBookSearch(query, pageNumber);

  const observer = useRef();
  const lastBookObserverRef = useCallback(
    node => {
      console.log(node);
    }, [])

  function handleSearch(e) {
    setQuery(e.target.value);
    setPageNumber(1);
  }

  return (
    <div className={appStyles.app}>
      <input className={appStyles.searchBox} type={"text"} value={query} onChange={handleSearch} />
      {books.map((data, index) => {
        if (index === data.length - 1) {
          return <p ref={lastBookObserverRef} key={index}>{data}</p>
        } else {
          return <p key={index} >{data}</p>
        }
      })}
      <div>Loading...</div>
      <div>Error</div>
    </div>
  );
}

export default App;
